export const sendRequest = (request: string) => ({
  type: 'SEND_REQUEST' as const,
  payload: request,
});

export const requestSuccess = (response: string) => ({
  type: 'REQUEST_SUCCESS' as const,
  payload: response,
});

export const requestError = (error: Error) => ({
  type: 'REQUEST_ERROR' as const,
  payload: error,
});
